package com.mariusreimer.rncppcode;

import android.os.Build;
import android.util.Log;

import androidx.annotation.RequiresApi;

import java.util.ArrayList;

import com.facebook.react.bridge.Arguments;
import com.facebook.react.bridge.Promise;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;
import com.facebook.react.bridge.ReadableArray;
import com.facebook.react.bridge.ReadableMap;
import com.facebook.react.bridge.WritableMap;
import com.facebook.react.module.annotations.ReactModule;

@ReactModule(name = RNCPPCodeModule.TAG)
public final class RNCPPCodeModule
    extends ReactContextBaseJavaModule {

  public static final String TAG = "RNCPPCode";

  static {
    try {
      System.loadLibrary("cpp-code");
      Log.d(TAG, "-------- libcpp-code: loaded");
    } catch (Exception e){
      Log.d(TAG, "-------- libcpp-code: loaded");
    }
  }

  RNCPPCodeModule(ReactApplicationContext reactContext) {
    super(reactContext);
  }

  @Override
  public String getName() {
    return TAG;
  }

  /**
   *
   */
  @ReactMethod
  public void multiply(final int a, final int b, final Promise promise) {
    long result = multiply(a, b);

    final WritableMap response = Arguments.createMap();

    response.putString("result", String.valueOf(result));

    promise.resolve(response);
  }

  private double[] convertReadableArrayOfDoublesToDoubleArray(ReadableArray readableArray) {
    int size = readableArray.size();
    double[] array = new double[size];
    for(int i = 0; i < size; i++) {
      array[i] = readableArray.getDouble(i);
    }
    return array;
  }

  @RequiresApi(api = Build.VERSION_CODES.N)
  @ReactMethod
  public void matchFingerPrint(ReadableMap unknownFingerPrint, ReadableArray existingFingerPrints, final Promise promise) {
    // Convert refs and points to doubles array
    double[] unknownRefs = convertReadableArrayOfDoublesToDoubleArray(unknownFingerPrint.getArray("refs"));
    double[] unknownPoints = convertReadableArrayOfDoublesToDoubleArray(unknownFingerPrint.getArray("points"));

    // Loop over existing fingerprints and compare against each
    int numExistingFingerPrints = existingFingerPrints.size();
    WritableMap results = Arguments.createMap();
    for(int i = 0; i < numExistingFingerPrints; i++) {
      ReadableMap existingFingerPrint = existingFingerPrints.getMap(i);
      double[] existingRefs = convertReadableArrayOfDoublesToDoubleArray(existingFingerPrint.getArray("refs"));
      double[] existingPoints = convertReadableArrayOfDoublesToDoubleArray(existingFingerPrint.getArray("points"));

      String id = existingFingerPrint.getString("id");
      double result = matchFingerPrint(unknownRefs, unknownPoints, existingRefs, existingPoints);
      results.putDouble(id, result);
    }

    WritableMap response = Arguments.createMap();
    response.putMap("result", results); // TODO catch errors and put on response.error
    promise.resolve(response);
  }

  public static native long multiply(long a, long b);
  public static native double matchFingerPrint(double[] unknownRefs, double[] unknownPoints, double[] existingRefs, double[] existingPoints);
}
